import React, {useState} from 'react';
import './App.css';
import Modal from './components/Modal';

function App() {
  var [title, setTitle] = useState(['첫번째 게시글', '두번째 게시글', '세번째 게시글']);
  var [count, setCount] = useState([1, 0, 3]);


  function titleChange(){
    // var newTitle = Array.from(title);
    var _title = [...title];
    _title[0] = '네번째 게시글'
    setTitle(_title);
  }
  return (
    <div className="App">
      <nav className="nav">
        <h1>React test page</h1>
      </nav>
      <input type="button" value="button" onClick={titleChange}/>
      <Contents/>
      {/* 컴포넌트는 대분자로 시작해야한다. */}
      <Modal/>
      {/* <Modal></Modal> */}
    </div>


  );

  function Contents(props) {
    var lists = [];
    var i = 0;
    var [_count, _setCount] = useState(count);

    while (i < title.length){
      lists.push(
        <div className = "list">
          <h3>{title[i]}
            <span onClick={()=>{
              _count = _count[i] + 1;
              setCount(_count);
            }}> ❤️ {count[i]}
            </span>
          </h3>
          <p>2021.9.{8+i}</p>
          <hr/>
        </div>
      )
      i = i + 1;
    }

    return(
      <>
      {lists}
      </>
    );
  }

}


export default App;
