//react-component-imageSlider
import React from 'react';
import ImageSlider from '../../ImageSlider';
import WorkTextBox from '../../WorkTextBox';
import LabIntro from '../../LabIntro';

const imageArray = [
    {
        id: "1",
        src:"../../images/dk_w_rld_logo1.jpg"
    },
    {
        id: "2",
        src:"../../images/dk_w_rld_logo2.jpg"
    },
    {
        id: "3",
        src:"../../images/dk_w_rld_logo3.jpg"
    },
    {
        id: "4",
        src:"../../images/dk_w_rld_logo4.jpg"
    },
]

const style = {
    margin: "0px auto",
    border: "1px solid red",
    maxWidth: "1000px"
}

function Labs01() {
    return (
        <>
        <LabIntro
        titlee="imageSlider"
        titlek="이미지 슬라이더"
        funcdesc=" 화면 변경될 때 마다 사이즈 변경하는 것으로 했더니 과부화 걸림
        //너무 잦은 반복으로 화면을 렌더링 해서 그런 듯
        //resizeBlankbox 함수를 debounce로 감싸고, 1000ms 동안 반복되지 않도록 설정
        //가장 마지막에 실행된 동일한 함수만을 적용한다.좌우 버튼을 누르면, 해당하는 버튼에 따라 이미지가 좌우로 이동하면서 다음 이미지로 변경"
        updatedesc={`2021.10.19 - ver 0.1 \n 2021.10.20 - ver 0.2`}/>
        <div style={style}>
            <ImageSlider img={imageArray} list='4'/>
        </div>
        <WorkTextBox
        desc={`다음∙이전 버튼을 누른다.
        ‣ 슬라이드 번호를 변경한다. \n
        슬라이드 번호가 변경된 것이 감지된다. 
        ‣ 슬라이드의 X값을 이동한다.`}
        size="md"
        />
        </>
    )
}

export default Labs01
