import React, {useState} from 'react';
import LabIntro from '../../LabIntro';

const state = {
    contents:[
    {
        id:1, 
        title:"HTML", 
        desc:"HTML is for information"
    },
    {
        id:2, 
        title:"CSS", 
        desc:"CSS is for design"
    },
    {
        id:3, 
        title:"JavaScript", 
        desc:"JavaScript is for interaction"
    }]
}


function Subject({title, desc}){
    return(
        <>
        <h1><a href="/" onClick={function(e){
            e.preventDefault();
        }}>{title}</a></h1>
        <p>{desc}</p>
        </>
    );
}

function ReadContent({title, desc}){
    return(
        <>
        <article>
            <h2>{title}</h2>
            <p>{desc}</p>
        </article>
        </>
    );
}

function ContentsList({data, onClick}){

    let lists = [];
    let i = 0;
    while(i<data.length){
        lists.push(
            <li key={data[i].id}>
                <a href="" onClick={onClick}>{data[i].title}</a>
            </li>
        );
        i = i + 1;
    }
    return(
        <nav>
            <ul>
                {lists}
            </ul>
        </nav>
    )
}

