        }}></button>
    )
}

function Labs05() {
    return (
        <>
            <LabIntro
            titlee="createReadUpdateDelete"
            titlek="생성확인수정삭제"
            funcdesc="서버에 데이터가 저장되진 않지만, CRUD 모델 구현"
            updatedesc={`2021.11.04 - start`}
            />
            <Subject
            title="Web"
            desc="Hello, world"
            />
            <ContentsList
            data={state.contents}
            onClick={(e)=>{
                e.preventDefault();
                console.log(state.contents.title[e]);
            }}
            />
        </>
    )
}

export default Labs05
