import React, {useEffect, useState, useRef} from 'react'
import styled from 'styled-components';

const imageArray = [
    {
        id: "1",
        src:"../../images/dk_w_rld_logo1.jpg"
    },
    {
        id: "2",
        src:"../../images/dk_w_rld_logo2.jpg"
    },
    {
        id: "3",
        src:"../../images/dk_w_rld_logo3.jpg"
    },
    {
        id: "4",
        src:"../../images/dk_w_rld_logo4.jpg"
    },
]

const Container = styled.div`
    position: relative;
    width: 500px;
    height: auto;
    margin: 0 auto;
    overflow: hidden;
    transform: translateY(50px);
    opacity: 1;
`;

const SliderContainer = styled.div`
    width: 500px;
    height: 500px;
    display: flex;
`;

const Button = styled.button`
  background-color: white;
  border-radius: 50%;
  width: 50px;
  height: 50px;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  display: flex;
  justify-content: center;
  align-items: center;
  left: ${(props) => props.direction === 'prev' && '1%'};
  right: ${(props) => props.direction === 'next' && '1%'};
  z-index: 200;

  img {
    width: 25%;
    height: 25%;
  }
`;

function ImageSlider({img, list}){
    const [currentSlide, setCurrentSlide] = useState(0);
    const slideRef = useRef(null);

    const handleSlideNext = () => {
        if(currentSlide >= list){
            setCurrentSlide(0);
        } else{
            setCurrentSlide(currentSlide + 1);
        }
        console.log("next");
    }
    const handleSlidePrev = () => {
        if(currentSlide === 0){
            setCurrentSlide(list);
        } else {
            setCurrentSlide(currentSlide - 1);
        }
        console.log("prev");
    }

    useEffect(() => {
        slideRef.current.style.transition = 'all 0.5s ease';
        slideRef.current.style.transform = `translateX(-${currentSlide}00%)`;
    },[currentSlide]);

    return(
        <Container>
            <SliderContainer ref={slideRef}>
            {img.map((image)=>{
                return <img key={image.id} src={image.src} alt="some" />    
            })}            
            </SliderContainer>
            <Button onClick={handleSlideNext} direction="next"></Button>
            <Button onClick={handleSlidePrev} direction="prev"></Button>
        </Container>
    );
}

function Labs01() {
    return (
        <>
        <ImageSlider img={imageArray}/>
        </>
    )
}

export default Labs01
