import React, {useState, useEffect} from 'react';

function Labs04() {
    const [scrollPostion, setScrollPosition] = useState(0);

    function onScroll(){
        setScrollPosition(window.scrollY);
    }

    useEffect(() => {
        window.addEventListener("scroll", onScroll);
        return () => {
            window.removeEventListener("scroll", onScroll);
        }
    },[]);

    return (
        <div style={{
            width : '100vw',
            overflow : 'hidden',
            margin : '0 auto',
            marginLeft: 'calc(-50vw + 50%)'
        }}>
            <div style={{
                width: '1200px',
                margin : '0 auto',
            }}>
                <div className="lab4-redbox" style={{
                    backgroundColor : 'red',
                    width : '500px',
                    height : '500px',
                    backgroundPositionX : scrollPostion ,
                    margin : '0 auto',
                    transform : `translateX(${scrollPostion*2}px)`
                }}></div>
                <div className="lab4-textbox" style={{
                    width : '500px',
                    height : '500px',
                    margin : '0 auto',
                    transform : `translateX(-${scrollPostion}px)`
                }}>Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</div> 
                <div className="lab4-bluebox" style={{
                    backgroundColor : 'blue', 
                    width : '500px',
                    height : '500px',
                    margin : '0 auto',
                    transform : `translateX(-${scrollPostion*2}px)`
                }}></div>

            </div>
        </div>
    );
}

export default Labs04
