function Labs04() {
    const [scrollPostion, setScrollPosition] = useState(0);

    function onScroll(){
        setScrollPosition(window.scrollY);
    }

    useEffect(() => {
        window.addEventListener("scroll", onScroll);
        return () => {
            window.removeEventListener("scroll", onScroll);
        }
    },[]);

    return (
        <>
           <div className="lab4-redbox" style={{
               backgroundColor : 'red',
               width : '500px',
               height : '500px',
               transform : `translateX${scrollPostion}px)`
           }}></div>
           <div className="lab4-bluebox" style={{
               backgroundColor : 'blue', 
               width : '500px',
               height : '500px',
               transform : `translateX(-${scrollPostion}px)`
           }}></div>
           <div className="lab4-textbox" style={{
               width : '500px',
               height : '500px',
               border : '1px solid red'
           }}>lsakdjf;laskdjf;laskdjf;laskdjf;laskjdf;laskdjf;laskdjfaoipeurpqw[oelksdvjl;asdk jfla</div> 
        </>
    );
}

export default Labs04
