import React, {useState} from 'react';
import '../About.css';

function About() {
    //초기 마진값 설정
    const [moveStyle, setMoveStyle] = useState(
        [
            {
                marginLeft: '600px'
            },
            {
                marginTop: '250px'
            }
        ] 
    );
    const [opacityStyle, setOpacityStyle] = useState(
        [
            {
                opacity: '0'
            },
            {
                opacity: '0'
            },
            {
                opacity: '0'
            },
            {
                opacity: '0'
            }
    ]
    );
    //1000ms가 흐르면, 
    const firstTransition = setTimeout(()=>{
        setMoveStyle([
            {
                marginLeft: '0px',
                transition: '1s ease'
            },
            {
                marginTop: '0px',
                transition: '1s ease'
            }
        ]);
        setOpacityStyle([
            {
                opacity: '1'
            },
            {
                opacity: '0'
            },
            {
                opacity: '0'
            },
            {
                opacity: '0'
            }
        ])

        clearTimeout(firstTransition);
    },1000);

    const secondTransition = setTimeout(()=>{
        setOpacityStyle([
            {
                opacity: '1'
            },
            {
                opacity: '1'
            },
            {
                opacity: '0'
            },
            {
                opacity: '0'
            }
        ])
        clearTimeout(secondTransition);
    }, 1500);
    const thirdTransition = setTimeout(()=>{
        setOpacityStyle([
            {
                opacity: '1'
            },
            {
                opacity: '1'
            },
            {
                opacity: '1'
            },
            {
                opacity: '0'
            }
        ])
        clearTimeout(thirdTransition);
    }, 2000);
    const forthTransition = setTimeout(()=>{
        setOpacityStyle([
            {
                opacity: '1'
            },
            {
                opacity: '1'
            },
            {
                opacity: '1'
            },
            {
                opacity: '1'
            }
        ])
        clearTimeout(forthTransition);
    }, 2500);
    return (
       <>
       <div className="about-greeting">
           <div className="about-greeting__text --en">
                <p>Hello.</p>
                <p><span className="outline-text">Designer</span>ㅤ―ㅤ<span className="outline-text">Developer</span></p>

                <p>I want to close the gap.</p>
                <p>I'm Daekyo Jeong.</p>
            </div>
            <div className="about-greeting__text --ko">
                <p>안녕하세요.</p>
                <div className="about-greeting__dd--virtical--ko">
                    <p className="outline-text">디자이너</p>
                    <p className="outline-text" style={moveStyle[1]}>디벨로퍼</p>
                </div>
                <div className="about-greeting__dd--ko">
                    <p className="outline-text">디자이너</p>
                    <p className="outline-text" style={moveStyle[0]}>디벨로퍼</p>
                </div>
                <p>간극을 좁혀보고픈</p>
                <p>정대교입니다.</p>
            </div>
        </div>
        <div className="about-introduce">
            <div className="about-introduce__text --ko">
                <h1>저는 이런 사람입니다.</h1>
                <br />
