import React, {useState} from 'react';
import './App.css';
import Modal from './components/Modal';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

function Home(){
  return(
    <>
    <h2>Home</h2>
    <p>Home..</p>
    </>
  );
}
function Topics(){
  return(
    <>
    <h2>Topics</h2>
    <p>Topics..</p>
    </>
  );
}
function Contact(){
  return(
    <>
    <h2>Contact</h2>
    <p>Contact..</p>
    </>
  );
}

function App() {
  var [title, setTitle] = useState(['첫번째 게시글', '두번째 게시글', '세번째 게시글']);
  var [count, setCount] = useState([1, 0, 3]);
  var _count = [...count];

  function titleChange(){
    // var newTitle = Array.from(title);
    var _title = [...title];
    _title[0] = '네번째 게시글'
    setTitle(_title);
  }
  return (
    <div className="App">
      <h1>React Router DOM exapmple</h1>
      <ul>
        <li><a href="/">Home</a></li>
        <li><a href="/topics">Topics</a></li>
        <li><a href="/contact">Contact</a></li>
      </ul>
      {/* 라우터와 일치하는 path를 가진 컴포넌트는 화면에 출력된다. 
      exact path를 사용하면 정확히 동일한 경우에만 작동한다 */}

      <Route exact path="/"><Home></Home></Route>
      <Route path="/topics"><Topics></Topics></Route>
      <Route path="/contact"><Contact></Contact></Route>

      {/* 스위치로 컴포넌트를 감싸면, 가장 먼저 감지되는 컨텐츠만 화면에 출력한다. 
      편법으로, 홈을 제일 뒤에 주면 가능*/}
      <Switch>
      <Route exact path="/"><Home></Home></Route>
      <Route path="/topics"><Topics></Topics></Route>
      <Route path="/contact"><Contact></Contact></Route>
      <Route path="/">Not Found</Route>
      </Switch>

      {/* <nav className="nav">
        <h1>React test page</h1> */}
      {/* </nav> */}
      {/* <input type="button" value="button" onClick={titleChange}/> */}
      {/* <Contents/> */}
      {/* 컴포넌트는 대분자로 시작해야한다. */}
      {/* <Modal/> */}
      {/* <Modal></Modal> */}
    </div>

  );

  function Contents(props) {
    var lists = [];
    var i = 0;

    console.log(_count);

    while (i < title.length){
      lists.push(
        <div className = "list">
          <h3>{title[i]}
            <span onClick={()=>{
              _count[0]++;
              console.log(i);
            }}> ❤️ {count[i]}
            </span>
          </h3>
          <p>2021.9.{8+i}</p>
          <hr/>
        </div>
      )
      i = i + 1;
    }

    return(
      <>
      {lists}
      </>
    );
  }

}


export default App;
