import React, {useEffect, useState, useRef} from 'react'
import styled from 'styled-components';

const Container = styled.div`
    position: relative;
    width: 600px;
    height: auto;
    margin: 0 auto;
    overflow: hidden;
    transform: translateY(50px);
    opacity: 1;
`;

const SliderContainer = styled.div`
    width: 500px;
    height: 500px;
    display: flex;
    transition: 0.5s ease;
`;

const Button = styled.button`
    color: white;
    width: 50px;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    display: flex;
    justify-content: center;
    align-items: center;
    left: ${(props) => props.direction === 'prev' && '1%'};
    right: ${(props) => props.direction === 'next' && '1%'};
    z-index: 200;
`;

function ImageSlider({img, list}){
    const [currentSlide, setCurrentSlide] = useState(0);
    const slideRef = useRef(null);

    const handleSlideNext = () => {
        if(currentSlide >= list-1){
            setCurrentSlide(0);
        } else{
            setCurrentSlide(currentSlide + 1);
        }
        console.log("next");
    }
    const handleSlidePrev = () => {
        if(currentSlide === 0){
            setCurrentSlide(list-1);
        } else {
            setCurrentSlide(currentSlide - 1);
        }
        console.log("prev");
    }

    useEffect(() => {
        // slideRef.current.style.transition = 'all 0.5s ease';
        slideRef.current.style.transform = `translateX(-${currentSlide}00%)`;
    },[currentSlide]);

    return(
        <Container>
            <SliderContainer ref={slideRef}>
            {img.map((image)=>{
                return <img key={image.id} src={image.src} alt="some" />    
            })}            
            </SliderContainer>
            <Button onClick={handleSlideNext} direction="next" value="next">next</Button>
            <Button onClick={handleSlidePrev} direction="prev" value="prev">prev</Button>
        </Container>
    );
}

export default ImageSlider
