import React, {useEffect, useState} from 'react'
import styled from 'styled-components';

const imageArray = [
    {
        id: "1",
        src:"../../images/dk_w_rld_logo1.jpg"
    },
    {
        id: "2",
        src:"../../images/dk_w_rld_logo2.jpg"
    },
    {
        id: "3",
        src:"../../images/dk_w_rld_logo3.jpg"
    },
    {
        id: "4",
        src:"../../images/dk_w_rld_logo4.jpg"
    },
]

const Container = styled.div`
    position: relative;
    width: 500px;
    height: auto;
    margin: 0 auto;
    overflow: hidden;
    transform: translateY(50px);
    opacity: 1;
`;

const SliderContainer = styled.div`
    width: 500px;
    height: 500px;
    display: flex;
`;

const Button = styled.button`
  background-color: white;
  border-radius: 50%;
  width: 50px;
  height: 50px;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  display: flex;
  justify-content: center;
  align-items: center;
  left: ${(props) => props.direction === 'prev' && '1%'};
  right: ${(props) => props.direction === 'next' && '1%'};
  z-index: 200;

  img {
    width: 25%;
    height: 25%;
  }
`;

function ImageSlider({img}){

    const handleSlideNext = () => {
        console.log("next");
    }
    const handleSlidePrev = () => {
        console.log("prev");
    }

    return(
        <Container>
            <SliderContainer>
            {img.map((image)=>{
                return <img key={image.id} src={image.src} alt="some" />    
            })}            
            </SliderContainer>
            <Button onClick={handleSlideNext} direction="next"></Button>
            <Button onClick={handleSlidePrev} direction="prev"></Button>
        </Container>
    );
}

function Labs01() {
    return (
        <>
        <ImageSlider img={imageArray}/>
        </>
    )
}

export default Labs01
