  return(
    <>
    <h2>Home</h2>
    <p>Home..</p>
    </>
  );
}
function Topics(){
  return(
    <>
    <h2>Topics</h2>
    <p>Topics..</p>
    </>
  );
}
function Contact(){
  return(
    <>
    <h2>Contact</h2>
    <p>Contact..</p>
    </>
  );
}

function App() {
  var [title, setTitle] = useState(['첫번째 게시글', '두번째 게시글', '세번째 게시글']);
  var [count, setCount] = useState([1, 0, 3]);
  var _count = [...count];

  function titleChange(){
    // var newTitle = Array.from(title);
    var _title = [...title];
    _title[0] = '네번째 게시글'
    setTitle(_title);
  }
  return (
    <div className="App">
      {/* <nav className="nav">
        <h1>React test page</h1> */}
      {/* </nav> */}
      {/* <input type="button" value="button" onClick={titleChange}/> */}
      {/* <Contents/> */}
      {/* 컴포넌트는 대분자로 시작해야한다. */}
      {/* <Modal/> */}
      {/* <Modal></Modal> */}
    </div>

  );

  function Contents(props) {
    var lists = [];
    var i = 0;

    console.log(_count);

    while (i < title.length){
      lists.push(
        <div className = "list">
          <h3>{title[i]}
            <span onClick={()=>{
              _count[0]++;
              console.log(i);
            }}> ❤️ {count[i]}
            </span>
          </h3>
          <p>2021.9.{8+i}</p>
          <hr/>
        </div>
      )
      i = i + 1;
    }

    return(
      <>
      {lists}
      </>
    );
  }

}


export default App;
